[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div class="print-heading">				<h2><div>Rent Payment</div>				</h2></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<style>
  .rent-print {
    font-family: "Arial", sans-serif;
    font-size: 14px;
    color: #333;
    max-width: 800px;
    margin: auto;
    padding: 20px;
  }
  .rent-print h2 {
    text-align: center;
    margin-bottom: 30px;
    color: #1a1a1a;
  }
  .rent-section {
    margin-bottom: 20px;
  }
  .rent-label {
    font-weight: bold;
    width: 200px;
    display: inline-block;
  }
  .rent-value {
    display: inline-block;
  }
  .rent-table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 15px;
  }
  .rent-table th,
  .rent-table td {
    border: 1px solid #ddd;
    padding: 8px 12px;
    text-align: left;
  }
  .rent-table th {
    background-color: #f2f2f2;
  }
  .footer-note {
    margin-top: 40px;
    font-size: 12px;
    text-align: center;
    color: #777;
  }
</style>

<div class="rent-print">
  <div class="rent-section">
    <div><span class="rent-label">Rent Payment No:</span> <span class="rent-value">{{ doc.name }}</span></div>
    <div><span class="rent-label">Payment Date:</span> <span class="rent-value">{{ doc.payment_date }}</span></div>
    <div><span class="rent-label">Status:</span> <span class="rent-value">{{ doc.status }}</span></div>
  </div>

  <div class="rent-section">
    <div><span class="rent-label">Shop Number:</span> <span class="rent-value">{{ doc.shop_number }}</span></div>
    <div><span class="rent-label">Shop Name:</span> <span class="rent-value">{{ doc.shop_name }}</span></div>
    <div><span class="rent-label">Tenant Name:</span> <span class="rent-value">{{ doc.tenant_name }}</span></div>
  </div>

  <table class="rent-table">
    <thead>
      <tr>
        <th>Description</th>
        <th>Duration (Months)</th>
        <th>Start Date</th>
        <th>Expiry Date</th>
        <th>Amount Paid</th>
      </tr>
    </thead>
    <tbody>
      <tr>
        <td>Monthly Rent</td>
        <td>{{ doc.subscription_duration }}</td>
        <td>{{ doc.subscription_start_date }}</td>
        <td>{{ doc.subscription_expiry_date }}</td>
        <td>{{ '{:,.0f}'.format(doc.amount_paid) }} YER</td>
      </tr>
    </tbody>
  </table>

  <br/>

  <div class="rent-section">
    <div><span class="rent-label">Remarks:</span> <span class="rent-value">{{ doc.remarks or "-" }}</span></div>
  </div>

  <div class="footer-note">
    This is a system-generated receipt. No signature is required.
  </div>
</div>
"}]
